      data: 'My custom message',  // Any string (optional)
      to: [],                     // Array of Connection objects (optional. Broadcast to everyone if empty)
      type: 'my-chat'             // The type of message (optional)
    })
    .then(() =
 {
        console.log('Message successfully sent');
    })
    .catch(error =
 {
        console.error(error);
    });




