        } else {
            // The SESSIONID wasn't valid
            return new ResponseEntity
(HttpStatus.INTERNAL_SERVER_ERROR);
        }
    } else {
        // The SESSION does not exist
        return new ResponseEntity
(HttpStatus.INTERNAL_SERVER_ERROR);
    }
}




